package com.aubga.java.currentArt.chapter08;import java.util.concurrent.CyclicBarrier;public class CyclicBarrierTest2 {    static CyclicBarrier c = new CyclicBarrier(2, new A());    public static void main(String[] args) {        new Thread(new Runnable() {            @Override            public void run() {                try {                    System.out.println(1);                    c.await();                } catch (Exception e) {                }            }        }).start();        try {            System.out.println(2);            c.await();        } catch (Exception e) {        }    }    static class A implements Runnable {        @Override        public void run() {            try {                System.out.println(3);                Thread.sleep(1000);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}